function CalculateEMI(t,r,n){var e=Math.pow(1+r/100/12,n),i=t*(r/100/12)*e/(e-1);return i/*=Math.ceil(100*i)/100*/}function NumberFormat(t){t=t.toString().replace(/[,]+/gi,""),t=t.toString().replace(/Rs./gi,"");var r="";t.indexOf(".")>0&&(r=t.substring(t.indexOf("."),t.length)),t=Math.floor(t),t=t.toString();var n=t.substring(t.length-3),e=t.substring(0,t.length-3);""!=e&&(n=","+n);var i=$.trim(e.replace(/\B(?=(\d{2})+(?!\d))/g,",")+n+r);return i}